<?php
require 'csrf.php'
require 'auth.php';
// منع الدخول الى هذه الصفحة لحسابات المشاهد
if (isset($_SESSION['account_type']) && $_SESSION['account_type'] === 'viewer') {
    header("Location: view_votes.php");
        exit;
        }
        $pageTitle = "إدخال الأصوات";
        require 'config.php';
        $message = "";
        include 'header.php';

        // جلب جميع المراكز وترتيبها أبجدياً حسب الاسم
        $centersStmt = $pdo->query("SELECT * FROM centers ORDER BY center_name");
        $centersList = $centersStmt->fetchAll();

        // جلب العدد الكلي للمرشحين
        $totalCandidates = $pdo->query("SELECT COUNT(*) FROM candidates")->fetchColumn();
        ?>
        <div class="card shadow-sm mb-4">
          <div class="card-body">
                <h2 class="card-title text-center mb-3">إدخال الأصوات</h2>
                      <!-- زر ترتيب المراكز المكتملة -->
                            <div class="mb-3 text-center">
                                        <button type="button" class="btn btn-outline-success btn-custom" onclick="sortCenters()">ترتيب المراكز المكتملة</button>
                                              </div>
                                                    <!-- حقل البحث عن المركز -->
                                                          <div class="mb-3">
                                                                      <input type="text" id="searchCenter" class="form-control" placeholder="ابحث عن مركز...">
                                                                            </div>
                                                                                  <!-- قائمة المراكز -->
                                                                                        <div id="centersList" class="list-group mb-3">
                                                                                                    <?php foreach ($centersList as $center): 
                                                                                                                          $centerId = $center['id'];
                                                                                                                                                // استعلام تجميعي لاستخراج الأصوات المدخلة لكل محطة داخل المركز
                                                                                                                                                                      $stmtVoteAgg = $pdo->prepare("SELECT station_id, COUNT(*) as vote_count FROM votes WHERE center_id = ? AND vote_count >= 0 GROUP BY station_id");
                                                                                                                                                                                            $stmtVoteAgg->execute([$centerId]);
                                                                                                                                                                                                                  $votesByStation = $stmtVoteAgg->fetchAll(PDO::FETCH_KEY_PAIR);

                                                                                                                                                                                                                                        // جلب إجمالي عدد المحطات للمركز
                                                                                                                                                                                                                                                              $stmtStations = $pdo->prepare("SELECT id FROM stations WHERE center_id = ?");
                                                                                                                                                                                                                                                                                    $stmtStations->execute([$centerId]);
                                                                                                                                                                                                                                                                                                          $stationsArr = $stmtStations->fetchAll();
                                                                                                                                                                                                                                                                                                                                $totalStations = count($stationsArr);

                                                                                                                                                                                                                                                                                                                                                      // حساب عدد المحطات المكتملة (أي التي تم إدخال أصوات لجميع المرشحين)
                                                                                                                                                                                                                                                                                                                                                                            $completeStations = 0;
                                                                                                                                                                                                                                                                                                                                                                                                  foreach ($stationsArr as $station) {
                                                                                                                                                                                                                                                                                                                                                                                                                            $stationId = $station['id'];
                                                                                                                                                                                                                                                                                                                                                                                                                                                      if (isset($votesByStation[$stationId]) && $votesByStation[$stationId] == $totalCandidates && $totalCandidates > 0) {
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                    $completeStations++;
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                              }
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                    }
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                          $centerButtonClass = ($totalStations > 0 && $completeStations == $totalStations)
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                    ? "list-group-item-success" : "list-group-item-secondary";
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                ?>
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                <button type="button" class="list-group-item <?php echo $centerButtonClass; ?> list-group-item-action"
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                    onclick="selectCenter(<?php echo $centerId; ?>, '<?php echo addslashes($center['center_name']); ?>')">
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                        <?php echo htmlspecialchars($center['center_name']); ?>
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                        </button>
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                    <?php endforeach; ?>
