        require 'config.php';

        function parseLogLine($line) {
            $result = [
                    'date'       => '',
                            'username'   => '',
                                    'operation'  => '',
                                            'vote_count' => '',
                                                    'center'     => '',
                                                            'station'    => '',
                                                                    'candidate'  => ''
                                                                        ];
                                                                            $patternAdd = '/^(?<date>\d{4}-\d{2}-\d{2} \d{2}:\d{2}:\d{2}) - (?<username>.+?) أضاف (?<vote_count>\d+) صوت(?: في)? في المركز (?<center>.+?) في المحطة (?<station>\d+) إلى المرشح (?<candidate>.+)$/u';
                                                                                $patternDel = '/^(?<date>\d{4}-\d{2}-\d{2} \d{2}:\d{2}:\d{2}) - (?<username>.+?) حذف (?<vote_count>\d+) صوت من المركز (?<center>.+?) في المحطة (?<station>\d+) للمرشح (?<candidate>.+)$/u';

                                                                                    if (preg_match($patternAdd, $line, $m)) {
                                                                                            $result['date']       = $m['date'];
                                                                                                    $result['username']   = $m['username'];
                                                                                                            $result['operation']  = 'إضافة';
                                                                                                                    $result['vote_count'] = $m['vote_count'];
                                                                                                                            $result['center']     = $m['center'];
                                                                                                                                    $result['station']    = $m['station'];
                                                                                                                                            $result['candidate']  = $m['candidate'];
                                                                                                                                                } elseif (preg_match($patternDel, $line, $m)) {
                                                                                                                                                        $result['date']       = $m['date'];
                                                                                                                                                                $result['username']   = $m['username'];
                                                                                                                                                                        $result['operation']  = 'حذف';
                                                                                                                                                                                $result['vote_count'] = $m['vote_count'];
                                                                                                                                                                                        $result['center']     = $m['center'];
                                                                                                                                                                                                $result['station']    = $m['station'];
                                                                                                                                                                                                        $result['candidate']  = $m['candidate'];
                                                                                                                                                                                                            }
                                                                                                                                                                                                                return $result;
                                                                                                                                                                                                                }

                                                                                                                                                                                                                // قراءة السجل
                                                                                                                                                                                                                $logsArray = [];
                                                                                                                                                                                                                if (file_exists('logs.txt')) {
                                                                                                                                                                                                                    $lines = file('logs.txt', FILE_IGNORE_NEW_LINES | FILE_SKIP_EMPTY_LINES);
                                                                                                                                                                                                                        // فرز تنازلي حسب التاريخ
                                                                                                                                                                                                                            usort($lines, function($a, $b) {
                                                                                                                                                                                                                                    return strtotime(substr($b,0,19)) - strtotime(substr($a,0,19));
                                                                                                                                                                                                                                        });
                                                                                                                                                                                                                                            foreach ($lines as $line) {
                                                                                                                                                                                                                                                    $logsArray[] = parseLogLine($line);
                                                                                                                                                                                                                                                        }
                                                                                                                                                                                                                                                            // استبدال اسم المستخدم بالاسم الكامل
                                                                                                                                                                                                                                                                foreach ($logsArray as &$entry) {
                                                                                                                                                                                                                                                                        $stmt = $pdo->prepare("SELECT fullname FROM users WHERE username = ?");
                                                                                                                                                                                                                                                                                $stmt->execute([$entry['username']]);
                                                                                                                                                                                                                                                                                        $full = $stmt->fetchColumn();
                                                                                                                                                                                                                                                                                                $entry['fullname'] = $full ?: $entry['username'];
                                                                                                                                                                                                                                                                                                    }
                                                                                                                                                                                                                                                                                                        unset($entry);
                                                                                                                                                                                                                                                                                                        }
                                                                                                                                                                                                                                                                                                        ?>
