if (!isset($_SESSION['login_attempts'])) {
    $_SESSION['login_attempts'] = 0;
    }
    if (!isset($_SESSION['lockout_level'])) {
        $_SESSION['lockout_level'] = 0;
        }

        $error = "";
        if (isset($_SESSION['login_error'])) {
            $error = $_SESSION['login_error'];
                unset($_SESSION['login_error']);
                }

                if ($_SERVER["REQUEST_METHOD"] === "POST") {
                    if (isset($_SESSION['lockout_until']) && time() < $_SESSION['lockout_until']) {
                            $remaining = $_SESSION['lockout_until'] - time();
                                    $minutes   = ceil($remaining / 60);
                                            $_SESSION['login_error'] = "تم تجاوز عدد المحاولات المسموح به. الرجاء الانتظار {$minutes} دقيقة.";
                                                    header("Location: login.php");
                                                            exit;
                                                                } else {
                                                                        if (isset($_SESSION['lockout_until']) && time() >= $_SESSION['lockout_until']) {
                                                                                    unset($_SESSION['lockout_until']);
                                                                                                $_SESSION['login_attempts'] = 0;
                                                                                                        }

                                                                                                                $username = trim($_POST["username"] ?? '');
                                                                                                                        $password = trim($_POST["password"] ?? '');

                                                                                                                                // التحقق من أدمن افتراضي
                                                                                                                                        if ($username === 'admin' && $password === 'admin123') {
                                                                                                                                                    $_SESSION['login_attempts'] = 0;
                                                                                                                                                                session_regenerate_id(true);
                                                                                                                                                                            $_SESSION["admin_logged_in"] = true;
                                                                                                                                                                                        $_SESSION['account_type']   = 'admin';
                                                                                                                                                                                                    $_SESSION['fullname']       = "أدمن افتراضي";
                                                                                                                                                                                                                header("Location: index.php");
                                                                                                                                                                                                                            exit;
                                                                                                                                                                                                                                    }

                                                                                                                                                                                                                                            // التحقق من قاعدة البيانات
                                                                                                                                                                                                                                                    $stmt = $pdo->prepare("SELECT * FROM users WHERE username = ?");
                                                                                                                                                                                                                                                            $stmt->execute([$username]);
