                                                                                                                                                                                                                                                                            if ($user && password_verify($password, $user['password'])) {
                                                                                                                                                                                                                                                                                        if (!empty($user['current_session_id'])) {
                                                                                                                                                                                                                                                                                                        $err = "هذا الحساب مُسجل دخول بالفعل من جهاز آخر. الرجاء تسجيل الخروج أولاً.";
                                                                                                                                                                                                                                                                                                                    } else {
                                                                                                                                                                                                                                                                                                                                    $_SESSION['login_attempts'] = 0;
