                                                                                                                                                                                                                                                                                                                                                                                                                                                                                            $_SESSION['fullname']     = $user['fullname'];
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                            $stmt2 = $pdo->prepare("UPDATE users SET current_session_id = ? WHERE id = ?");
