                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                            exit;
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                    } else {
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                // محاولة فاشلة
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                            $_SESSION['login_attempts']++;
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                        if ($_SESSION['login_attempts'] >= MAX_LOGIN_ATTEMPTS) {
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                        $lvl      = $_SESSION['lockout_level'];
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                        $duration = $lockoutDurations[$lvl];
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                        $_SESSION['lockout_until'] = time() + $duration;
