                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                        $_SESSION['login_attempts'] = 0;
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                        $err = "تم حظر الدخول مؤقتاً لمدة " . ceil($duration/60) . " دقيقة بعد 5 محاولات فاشلة.";
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                    } else {
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                    $remaining = MAX_LOGIN_ATTEMPTS - $_SESSION['login_attempts'];
