        /**
         * دالة لمحاولة إنهاء جلسة مستخدم آخر عن طريق حذف ملف الجلسة.
          * تأكد من أن مسار تخزين الجلسات يسمح بالوصول إلى ملفات الجلسات.
           */
           function terminateUserSession($targetSessionId) {
               if (!empty($targetSessionId)) {
                       // الحصول على مسار تخزين الجلسات
                               $sessionPath = session_save_path();
                                       if (empty($sessionPath)) {
                                                   $sessionPath = sys_get_temp_dir();
                                                           }
                                                                   // صيغة ملف الجلسة عادة تكون sess_{session_id}
                                                                           $sessionFile = $sessionPath . DIRECTORY_SEPARATOR . "sess_" . $targetSessionId;
                                                                                   if (file_exists($sessionFile)) {
                                                                                               @unlink($sessionFile);
                                                                                                       }
                                                                                                           }
                                                                                                           }

                                                                                                           // معالجة حذف المستخدم عند استقبال معامل GET "delete"
                                                                                                           if (isset($_GET['delete'])) {
                                                                                                               $id = (int) $_GET['delete'];
                                                                                                                   // استرجاع سجل المستخدم قبل الحذف للتحقق من session id الخاص به
                                                                                                                       $stmt = $pdo->prepare("SELECT current_session_id FROM users WHERE id = ?");
                                                                                                                           $stmt->execute([$id]);
                                                                                                                               $userRecord = $stmt->fetch();
                                                                                                                                   $targetSessionId = $userRecord ? $userRecord['current_session_id'] : "";

                                                                                                                                       // تنفيذ عملية الحذف من قاعدة البيانات
                                                                                                                                           $stmt = $pdo->prepare("DELETE FROM users WHERE id = ?");
                                                                                                                                               $stmt->execute([$id]);

                                                                                                                                                   // إذا كان لهذا الحساب جلسة مسجلة، قم بتفريغها وإنهائها
                                                                                                                                                       if (!empty($targetSessionId)) {
                                                                                                                                                               $stmt = $pdo->prepare("UPDATE users SET current_session_id = '' WHERE id = ?");
                                                                                                                                                                       $stmt->execute([$id]);
                                                                                                                                                                               terminateUserSession($targetSessionId);
                                                                                                                                                                                   }
                                                                                                                                                                                       header("Location: manage_accounts.php?msg=" . urlencode("تم حذف المستخدم بنجاح."));
                                                                                                                                                                                           exit;
                                                                                                                                                                                           }

                                                                                                                                                                                           // معالجة تحديث المستخدم عند إرسال نموذج التعديل من النافذة المنبثقة
                                                                                                                                                                                           if ($_SERVER["REQUEST_METHOD"] == "POST" && isset($_POST['update_user'])) {
                                                                                                                                                                                               $id = (int) $_POST['user_id'];
                                                                                                                                                                                                   $fullname = trim($_POST['fullname']);
                                                                                                                                                                                                       $username = trim($_POST['username']);
                                                                                                                                                                                                           $password = trim($_POST['password']);
                                                                                                                                                                                                               $account_type = trim($_POST['account_type']);

                                                                                                                                                                                                                   // إذا كان المستخدم الحالي وكيلاً فلا يُسمح له بتحويل الحساب إلى "وكيل"
                                                                                                                                                                                                                       if ($_SESSION['account_type'] === 'agent' && $account_type === 'agent') {
                                                                                                                                                                                                                               $error = "ليس لديك صلاحية تعديل حسابات الوكلاء.";
                                                                                                                                                                                                                                   } else {
                                                                                                                                                                                                                                           if (!empty($password)) {
                                                                                                                                                                                                                                                       $hashed_password = password_hash($password, PASSWORD_DEFAULT);
                                                                                                                                                                                                                                                                   $encrypted_password = openssl_encrypt($password, 'AES-256-CBC', ENCRYPTION_KEY, 0, ENCRYPTION_IV);
                                                                                                                                                                                                                                                                               $stmt = $pdo->prepare("UPDATE users SET fullname = ?, username = ?, password = ?, password_enc = ?, account_type = ? WHERE id = ?");
                                                                                                                                                                                                                                                                                           $stmt->execute([$fullname, $username, $hashed_password, $encrypted_password, $account_type, $id]);
                                                                                                                                                                                                                                                                                                   } else {
                                                                                                                                                                                                                                                                                                               $stmt = $pdo->prepare("UPDATE users SET fullname = ?, username = ?, account_type = ? WHERE id = ?");
                                                                                                                                                                                                                                                                                                                           $stmt->execute([$fullname, $username, $account_type, $id]);
                                                                                                                                                                                                                                                                                                                                   }
                                                                                                                                                                                                                                                                                                                                           // استرجاع session id للحساب الذي تم تعديله
                                                                                                                                                                                                                                                                                                                                                   $stmt = $pdo->prepare("SELECT current_session_id FROM users WHERE id = ?");
                                                                                                                                                                                                                                                                                                                                                           $stmt->execute([$id]);
                                                                                                                                                                                                                                                                                                                                                                   $userRecord = $stmt->fetch();
                                                                                                                                                                                                                                                                                                                                                                           $targetSessionId = $userRecord ? $userRecord['current_session_id'] : "";
                                                                                                                                                                                                                                                                                                                                                                                   // إذا كان لديه جلسة مسجلة، قم بإزالتها وإنهائها
                                                                                                                                                                                                                                                                                                                                                                                           if (!empty($targetSessionId)) {
                                                                                                                                                                                                                                                                                                                                                                                                       $stmt = $pdo->prepare("UPDATE users SET current_session_id = '' WHERE id = ?");
                                                                                                                                                                                                                                                                                                                                                                                                                   $stmt->execute([$id]);
                                                                                                                                                                                                                                                                                                                                                                                                                               terminateUserSession($targetSessionId);
                                                                                                                                                                                                                                                                                                                                                                                                                                       }
                                                                                                                                                                                                                                                                                                                                                                                                                                               header("Location: manage_accounts.php?msg=" . urlencode("تم تحديث بيانات المستخدم بنجاح."));
                                                                                                                                                                                                                                                                                                                                                                                                                                                       exit;
                                                                                                                                                                                                                                                                                                                                                                                                                                                           }
                                                                                                                                                                                                                                                                                                                                                                                                                                                           }

                                                                                                                                                                                                                                                                                                                                                                                                                                                           $msg = "";
                                                                                                                                                                                                                                                                                                                                                                                                                                                           if (isset($_GET['msg'])) {
                                                                                                                                                                                                                                                                                                                                                                                                                                                               $msg = $_GET['msg'];
                                                                                                                                                                                                                                                                                                                                                                                                                                                               }

                                                                                                                                                                                                                                                                                                                                                                                                                                                               // جلب بيانات المستخدمين؛ إذا كان المستخدم الحالي "وكيل" فلا تُعرض حسابات الوكلاء
                                                                                                                                                                                                                                                                                                                                                                                                                                                               if ($_SESSION['account_type'] === 'agent') {
                                                                                                                                                                                                                                                                                                                                                                                                                                                                   $stmt = $pdo->prepare("SELECT * FROM users WHERE account_type <> 'agent' ORDER BY fullname");
                                                                                                                                                                                                                                                                                                                                                                                                                                                                       $stmt->execute();
                                                                                                                                                                                                                                                                                                                                                                                                                                                                           $users = $stmt->fetchAll();
                                                                                                                                                                                                                                                                                                                                                                                                                                                                           } else {
                                                                                                                                                                                                                                                                                                                                                                                                                                                                               $stmt = $pdo->query("SELECT * FROM users ORDER BY fullname");
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                   $users = $stmt->fetchAll();
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                   }

                                                                                                                                                                                                                                                                                                                                                                                                                                                                                   // دالة لفك تشفير كلمة المرور
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                   function getDecryptedPassword($encrypted) {
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                       if (empty($encrypted)) {
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                               return "";
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                   }
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                       return openssl_decrypt($encrypted, 'AES-256-CBC', ENCRYPTION_KEY, 0, ENCRYPTION_IV);
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                       }
